import{_ as o}from"./chunks/ArticleMetadata.1_--bNMz.js";import{_ as s,D as h,o as r,c,I as n,w as u,k as i,a as b,R as _,b as m,e as p}from"./chunks/framework.Dn7lIAzt.js";import"./chunks/md5.vgHT5zCM.js";const x=JSON.parse('{"title":"软件设计师知识点速记：标准与知识产权","description":"","frontmatter":{"title":"软件设计师知识点速记：标准与知识产权","author":"查尔斯","date":"2023/05/08 21:22","isOriginal":false,"categories":["碎片化知识点"],"tags":["软考","软件设计师"]},"headers":[],"relativePath":"categories/fragments/2023/05/08/软件设计师知识点速记3.md","filePath":"categories/fragments/2023/05/08/软件设计师知识点速记3.md","lastUpdated":1713341239000}'),f={name:"categories/fragments/2023/05/08/软件设计师知识点速记3.md"},k=i("h1",{id:"软件设计师知识点速记-标准与知识产权",tabindex:"-1"},[b("软件设计师知识点速记：标准与知识产权 "),i("a",{class:"header-anchor",href:"#软件设计师知识点速记-标准与知识产权","aria-label":'Permalink to "软件设计师知识点速记：标准与知识产权"'},"​")],-1),g=_('<h2 id="保护范围与对象" tabindex="-1">保护范围与对象 <a class="header-anchor" href="#保护范围与对象" aria-label="Permalink to &quot;保护范围与对象&quot;">​</a></h2><table><thead><tr><th>法律法规名称</th><th>保护对象及范围</th><th>注意事项</th></tr></thead><tbody><tr><td>著作权法（版权）</td><td>著作权（文学、绘画、摄影等作品）</td><td>1、<strong>不需要申请</strong> ，作品完成即开始保护；<br>2、绘画或摄影作品原件出售（赠予）著作权还归原作者，原件拥有者有：所有权、展览权。</td></tr><tr><td>软件著作权法<br>计算机软件保护条例</td><td>软件著作权<br>软件作品</td><td>1、<strong>不需要申请</strong> ，作品完成即开始保护；<br>2、登记制度便于举证。</td></tr><tr><td>专利法</td><td>专利权</td><td>需要申请，专利权有效期是从申请日开始计算。</td></tr><tr><td>商标法</td><td>商标权</td><td>需要申请，核准之日起商标受保护。</td></tr><tr><td>反不正当竞争法</td><td>商业秘密权</td><td>1、商业秘密包括技术与经营两个方面；<br>2、必须有保密措施才能认定商业秘密。</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">TIP</p><ul><li>著作权是提高对作品，包括文学、艺术、自然科学、社会科学和工程技术领域内具有独创性并能以某种有形形式复制的智力成果。</li><li>专利权保护的是具有创造性的发明及设计等成果。</li><li>商业秘密权用来保护商家的秘密（例如：软件的技术信息及经营信息属于商业秘密范畴）。</li><li>计算机软件既是作品，又是一种使用工具，还是一种工业产品(商品)，具备作品性、工具性、商业性特征。因此对于计算机软件保护来说，仅依靠某项法律或法规不能解决软件的所有知识产权问题， 需要利用多层次的法律保护体系对计算机软件实施保护。我国已形成了比较完备的计算机软件知识产权保护的法律体系，即已形成以著作权法、计算机软件保护条例、计算机软件著作权登记办法保护为主，以专利法、反不正当竞争法、合同法、商标法、刑法等法律法规为辅的多层次保护体系，可对计算机软件实施交叉和重叠保护。在这样的保护体系下，计算机软件能够得到全面的、适度的保护。例如，计算机软件符合专利法所保 护的法定主题，就可以申请专利，利用专利法来保护其中符合发明创造条件的创造性成 果。对于那些为极少数专门用户开发的专用软件，可以利用反不正当竞争法中的商业秘密权和合同法来保护其中的技术秘密。我国没有专门针对知识产权制定统一的法律(知识产权法)，而是在民法通则规定的原则下，根据知识产权的不同类型制定了不同的单项法律及法规，如著作权法、商标法、专利法、计算机软件保护条例等，这些法律、法规共同构成了我国保护知识产权的法律体系。</li></ul></div><h2 id="保护期限" tabindex="-1">保护期限 <a class="header-anchor" href="#保护期限" aria-label="Permalink to &quot;保护期限&quot;">​</a></h2><table><thead><tr><th>客体类型</th><th>权力类型</th><th>保护期限</th></tr></thead><tbody><tr><td>公民作品</td><td>署名权、修改权、保护作品完整权</td><td>没有限制</td></tr><tr><td></td><td>发表权、使用权和获得报酬权</td><td>作者终生及其死亡后的50年（第50年的12月31日）</td></tr><tr><td>单位作品</td><td>发表权、使用权和获得报酬权</td><td>50年（首次发表后的第50年的12月31日），若期间未发表，不保护</td></tr><tr><td>公民软件产品</td><td>署名权、修改权</td><td>没有限制</td></tr><tr><td></td><td>发表权、复制权、发行权、出租权、信息网络传播权、翻译权、使用许可权、获得报酬权、转让权</td><td>作者终生及其死亡后的50年（第50年的12月31日）。合作开发，以最后死亡作者为准。</td></tr><tr><td>单位软件产品</td><td>发表权、复制权、发行权、出租权、信息网络传播权、翻译权、使用许可权、获得报酬权、转让权</td><td>50年（首次发表后的第50年的12月31日），若期间未发表，不保护</td></tr><tr><td>注册商标</td><td></td><td>有效期10年（若注册人死亡或倒闭1年后，未转移则可注销，期满前6个月内必须续住）</td></tr><tr><td>发明专利权</td><td></td><td>保护期为20年（从申请日开始）</td></tr><tr><td>实用新型和外观设计专利权</td><td></td><td>保护期为10年（从申请日开始）</td></tr><tr><td>商业秘密</td><td></td><td>不确定（相当于无期限限制），公开后公众可用</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">TIP</p><ul><li>保护期限不受限制的有：署名权、修改权、保护作品完整权。保护期限为作者终身及死后50年的，包括：发表权、使用权和获得报酬权。</li><li>根据《中华人民共和国商标法》第三十八条：注册商标有效期满，需要继续使用的，应当在期满前六个月内申请续展注册。专利权和著作权到期后都无法延长，商业秘密权无期限限制。</li><li>根据我国法律法规的规定，烟草类商品必须使用注册商标。</li><li>根据《中华人民共和国商标法》第十条：下列标志不得作为商标使用，县级以上行政区划的地名或者公众知晓的外国地名，不得作为商标。但是，地名具有其他含义或者作为集体商标、证明商标组成部分的除外；已经注册的使用地名的商标继续有权。</li></ul></div><h2 id="知识产权人确定" tabindex="-1">知识产权人确定 <a class="header-anchor" href="#知识产权人确定" aria-label="Permalink to &quot;知识产权人确定&quot;">​</a></h2><table><thead><tr><th>情况说明</th><th>判断说明</th><th>归属</th></tr></thead><tbody><tr><td>作品（职务作品）</td><td>利用单位的物质技术条件进行创作，并由单位承担责任的</td><td>除署名权外其他著作权归单位</td></tr><tr><td></td><td>有合同约定，其著作权属于单位</td><td>除署名权外其他著作权归单位</td></tr><tr><td></td><td>其他</td><td>作者拥有著作权，单位有权在业务范围内优先使用</td></tr><tr><td>软件（职务作品）</td><td>属于本职工作中明确规定的开发目标</td><td>单位享有著作权</td></tr><tr><td></td><td>属于从事本职工作活动的结果</td><td>单位享有著作权</td></tr><tr><td></td><td>使用了单位资金、专用设备、未公开的信息等物质、技术条件，并由单位或组织承担责任的软件</td><td>单位享有著作权</td></tr><tr><td>专利权（职务作品）</td><td>本职工作中作出的发明创造</td><td>单位享有专利</td></tr><tr><td></td><td>履行本单位交付的本职工作之外的任务所作出的的发明创造</td><td>单位享有专利</td></tr><tr><td></td><td>离职、退休或调动工作后1年内，与原单位工作相关</td><td>单位享有专利</td></tr><tr><td>作品软件（委托创作）</td><td>有合同约定，著作权归委托方</td><td>委托方</td></tr><tr><td></td><td>合同中未约定著作权归属</td><td>创作方</td></tr><tr><td>作品软件（合作开发）</td><td>只进行组织，提供咨询意见、物质条件或者进行其他辅助工作</td><td>不享有著作权</td></tr><tr><td></td><td>共同创作的</td><td>共同享有，按人头比例。成果可分割的，可分开申请。</td></tr><tr><td>商标</td><td>在同一种商品或者类似商品上</td><td>谁先申请谁拥有（除知名商标的非法抢注）<br>同时申请（同日申请），则根据谁先使用（需提供证据）<br>无法提供证据，协商归属，无效时使用抽签（但不可不确定）</td></tr><tr><td>专利</td><td></td><td>谁先申请谁拥有<br>同时申请（同日申请），则协商归属</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">TIP</p><ul><li>根据“同一的发明创造只能被授予一项专利”的规定，在同一天，两个不同的人就同样的发明创造申请专利的，专利局将分别向各申请人通报有关情况，请他们自己去协商解决这一问题，解决的方法一般有两种，一种是两申请人作为一件申请的共同申请人；另一种是其中一方放弃权利并从另一方得到适当的补偿。</li><li>根据专利法实施细则，职务发明创造是指： <ul><li>在本职工作中做出的发明创造</li><li>履行本单位交付的本职工作之外的任务所做出的发明创造</li><li>退职、退休或者调动工作后 <strong>1年内</strong> 做出的，与其在原单位承担的本职工作或者原单位分配的任务有关的发明创造。</li></ul></li></ul></div><h2 id="侵权判定" tabindex="-1">侵权判定 <a class="header-anchor" href="#侵权判定" aria-label="Permalink to &quot;侵权判定&quot;">​</a></h2><ul><li>中国公民、法人或者其他组织的作品，不论是否发表，都享有著作权。</li><li>开发软件所用的思想、处理过程、操作方法或者数学概念不受保护。</li><li>著作权法不适用于下列情形： <ul><li>法律、法规，国家机关的决议、决定、命令和其他具有立法、行政、司法性质的文件，及其官方正式译文；</li><li>时事新闻；</li><li>历法、通用数表、通用表格和公式。</li></ul></li></ul><table><thead><tr><th>不侵权</th><th>侵权</th></tr></thead><tbody><tr><td>个人学习、研究或者欣赏</td><td>未经许可，发表他人作品</td></tr><tr><td>适当引用</td><td>未经合作作者许可，将与他人合作创作的作品当作自己单独创作的作品发表的</td></tr><tr><td>公开演讲内容</td><td>未参加创作，在他人作品署名</td></tr><tr><td>用于教学或科学研究</td><td>歪曲、篡改他人作品的</td></tr><tr><td>复制馆藏作品</td><td>剽窃他人作品的</td></tr><tr><td>免费表演他人作品</td><td>使用他人作品，未付报酬</td></tr><tr><td>室外公共场所艺术品临摹、绘画、摄影、录像</td><td>未经出版者许可，使用其出版的图书、期刊的版式设计的</td></tr><tr><td>将汉语作品译成少数民族语言作品或盲文出版</td><td></td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">TIP</p><ul><li>如某美国专利在中国没有申请，则在中国不享有专利权，即在中国销售，中国企业不需要向美国公司支付美国专利的许可使用费。</li></ul></div><h2 id="标准化基础知识" tabindex="-1">标准化基础知识 <a class="header-anchor" href="#标准化基础知识" aria-label="Permalink to &quot;标准化基础知识&quot;">​</a></h2><h3 id="标准的分类" tabindex="-1">标准的分类 <a class="header-anchor" href="#标准的分类" aria-label="Permalink to &quot;标准的分类&quot;">​</a></h3><ul><li>国际标准：ISO、IEC等国际标准化组织</li><li>国家标准： <ul><li>GB：中国</li><li>ANSI：美国</li><li>BS：英国</li><li>JIS：日本</li></ul></li><li>区域标准：又称为地区标准 <ul><li>PASC：太平洋地区标准会议</li><li>CEN：欧洲标准委员会</li><li>ASAC：亚洲标准咨询委员会</li><li>ARSO：非洲地区标准化组织</li></ul></li><li>行业标准： <ul><li>GJB：中国军用标准</li><li>MIT-S：美国军用标准</li><li>IEEE：美国电气电子工程师协会</li></ul></li><li>地方标准：国家的地方一级行政机构制定的标准</li><li>企业标准</li><li>项目规范</li></ul><h3 id="标准的编号" tabindex="-1">标准的编号 <a class="header-anchor" href="#标准的编号" aria-label="Permalink to &quot;标准的编号&quot;">​</a></h3><ul><li>国际、国外标准代号：标准代号+专业类号+顺序号+年代号</li><li>我国国家标准代号： <ul><li>GB：强制性标准代号</li><li>GB/T：推荐性标准代号</li><li>GB/Z：指导性标准代号</li><li>GSB：实物标准代号</li></ul></li><li>行业标准代号：由汉语拼音大写字母组成（如电子行业为SJ）</li><li>地方标准代号：由 DB 加上省级行政区代码的前两位</li><li>企业标准代号：由 Q 加上企业代号组成</li></ul>',18);function P(t,S,q,I,T,B){const a=o,e=h("ClientOnly");return r(),c("div",null,[k,n(e,null,{default:u(()=>{var d,l;return[(((d=t.$frontmatter)==null?void 0:d.aside)??!0)&&(((l=t.$frontmatter)==null?void 0:l.showArticleMetadata)??!0)?(r(),m(a,{key:0,article:t.$frontmatter},null,8,["article"])):p("",!0)]}),_:1}),g])}const A=s(f,[["render",P]]);export{x as __pageData,A as default};
